    #region JSON SETTINGS
    /// <summary>
    /// JSON SETTINGS
    /// </summary>
    public static class NewtonsoftDefaultSettings
    {
        public static void Default(Newtonsoft.Json.JsonSerializerSettings settings)
        {
            settings.NullValueHandling = Newtonsoft.Json.NullValueHandling.Include;
            settings.MissingMemberHandling = Newtonsoft.Json.MissingMemberHandling.Ignore;
            settings.ConstructorHandling = Newtonsoft.Json.ConstructorHandling.Default;
            settings.TypeNameHandling = Newtonsoft.Json.TypeNameHandling.None;
            settings.ContractResolver = new Newtonsoft.Json.Serialization.CamelCasePropertyNamesContractResolver();
            settings.DateFormatString = "yyyy-MM-dd HH:mm:ss";
        }
    }
    #endregion

