        #region Http Post
        public static async Task<T> HttpPost<T>(string url, dynamic postData)
        {
            try
            {
                HttpClientHandler handler = new();
                handler.ServerCertificateCustomValidationCallback = (message, cert, chain, error) => true;
                using HttpClient http = new(handler);
                http.DefaultRequestHeaders.Add("ContentType", "application/json");
                StringContent content = new(Newtonsoft.Json.JsonConvert.SerializeObject(postData));
                content.Headers.ContentType = new MediaTypeHeaderValue("application/json");
                HttpResponseMessage response = await http.PostAsync(url, content);
                if (response.StatusCode == System.Net.HttpStatusCode.OK)
                {
                    string results = await response.Content.ReadAsStringAsync();

                    return Newtonsoft.Json.JsonConvert.DeserializeObject<T>(results);
                }
                else
                {
                    Log($"错误代码：{response.StatusCode}", "Helper.HttpPost");
                    return default;
                }
            }
            catch (Exception ex)
            {
                Log(ex.Message, "Helper.HttpPost");
                return default;
            }
        }
        #endregion

        #region Log
        /// <summary>
        /// Log the specified type and content.
        /// </summary>
        /// <param name="type">Type.</param>
        /// <param name="content">Content.</param>
        public static void Log(string content = "", string type = "")
        {
            var dir = $"{AppDomain.CurrentDomain.BaseDirectory}Logs";
            if (!Directory.Exists(dir))
                Directory.CreateDirectory(dir);
            var path = $"{dir}/{DateTime.Today:yyMMdd}.txt";
            using StreamWriter mySw = File.AppendText(path);
            string write_content = DateTime.Now.ToString("yyyy-MM-dd HH:mm:ss.fff") + " " + (string.IsNullOrEmpty(type) ? " Info" : type) + " " + content;
            mySw.WriteLine(write_content);
            mySw.Close();
        }
        #endregion

