        /// <summary>
        /// 加密
        /// </summary>
        /// <param name="str">待加密字符</param>
        /// <returns>加密字符</returns>
        public static string Encrypt(string str)
        {
            var hash = SHA1.Create().ComputeHash(Encoding.UTF8.GetBytes(str));

            return Convert.ToBase64String(hash);
        }

        /// <summary>
        /// MD5加密
        /// </summary>
        /// <param name="str"></param>
        /// <returns></returns>
        public static string EncryptMD5(string str)
        {
            var md5 = MD5.Create();
            byte[] bytes = Encoding.UTF8.GetBytes(str);
            return Convert.ToBase64String(md5.ComputeHash(bytes));
        }

        public static string EncryptMD5LowerCase(string str)
        {
            var md5 = MD5.Create();
            byte[] bytes = Encoding.UTF8.GetBytes(str);
            return BitConverter.ToString(md5.ComputeHash(bytes)).Replace("-", "").ToLower();
        }
