        #region 汉字转拼音首字母
        /// <summary>
        /// 汉字转拼音首字母
        /// </summary>
        /// <param name="chs"></param>
        /// <returns></returns>
        public static string Chs2PY(string chs, bool upperCase)
        {
            string strPY = "";
            string res = "";
            char[] chars = chs.ToCharArray();
            foreach (char ch in chars)
            {
                int chCode = System.Convert.ToInt32(ch);
                if (chCode > 40869 || chCode < 19968)
                    res += ch.ToString();
                else
                    res += strPY.Substring(chCode - 19968, 1);
            }

            if (upperCase)
                res = res.ToUpper();

            return res;
        }
        #endregion

        #region CopyObjectProperty
        public static void CopyObjectProperty<T>(T tSource, T tDestination) where T : class
        {
            //获得所有property的信息
            System.Reflection.PropertyInfo[] properties = tSource.GetType().GetProperties();
            foreach (System.Reflection.PropertyInfo p in properties)
            {
                if (p.Name.ToLower() != "isbasepoco")
                    p.SetValue(tDestination, p.GetValue(tSource, null), null);//设置tDestination的属性值              
            }
        }
        #endregion

        #region INI
        public static string ReadConfig(string config)
        {
            INI ini = new($"{AppDomain.CurrentDomain.BaseDirectory}Settings.ini");
            return ini.ReadString("Settings", config, "");
        }

        public static void SetConfig(string config, string value)
        {
            INI ini = new($"{AppDomain.CurrentDomain.BaseDirectory}Settings.ini");
            ini.WriteString("Settings", config, value);
            ini.Update();
        }
        #endregion
